// Code pour générer la liste de Pokémon dans le fichier HTML
window.addEventListener("DOMContentLoaded", function () {
    const pokemonListElement = document.getElementById("pokemon-list");

    // Fonction pour créer un élément de liste pour chaque Pokémon
    function createPokemonListItem(pokemon) {
        const listItem = document.createElement("li");
        listItem.textContent = pokemon.pokemon;
        return listItem;
    }

    // Route pour récupérer la liste de Pokémon depuis le serveur
    fetch("/pokemons")
        .then((response) => response.json())
        .then((data) => {
            data.forEach((pokemon) => {
                const listItem = createPokemonListItem(pokemon);
                pokemonListElement.appendChild(listItem);
            });
        })
        .catch((error) => console.log(error));
});
